import React from "react";
// import { taskList,setTaskList } from "./App";

const Task = (props) => {
    
  return (
    <>
      <li className="toDoItem done">
        <p className="line"></p>
        <span className="checkbox">
          <i
            className="fa-solid fa-check-double green"
            style={{ fontSize: "1.4rem" }}
          ></i>
        </span>
        <span className="toDoTitle">{props.taskName}</span>
        <button onClick={props.onSelect}>
          <i
            className="fa-solid fa-trash-can red"
            style={{ fontSize: "1.4rem" }}
            key = {props.id}
          ></i>
        </button>
      </li>
    </>
  );
};
export default Task;
