import { isEditable } from "@testing-library/user-event/dist/utils";
import React from "react";
import { useState } from "react";
// import { taskList,setTaskList } from "./App";

const Task = (props) => {
  const { taskList, handleEdit, checked, onCheck, taskName, id, onSelect } =
    props;
  const [isEditing, setIsEditing] = useState(false);
  const handleEditing = (e) => {
    let newList = [...taskList];
    console.log(e);
    setIsEditing(!isEditing);
    if (isEditing) {
      console.log(isEditing);
      const text = e.target.parentElement.innerText;
      console.log(typeof text);
      newList[parseInt(e.target.id)] = text;
      newList = newList.map((task, i) => {
        if (i === e.target.id) {
          task = text;
        }
        return task;
      });
      console.log(newList);
      handleEdit(newList);
    }
    console.log(e.target.parentElement.innerText);
    console.log("Editing : " + e.target.id);
  };
  return (
    <>
      <li
        className="toDoItem done"
        style={{ opacity: checked ? "50%" : "100%" }}
      >
        {checked ? (
          <>
            <p className="line"></p>
          </>
        ) : (
          <></>
        )}
        <span className="checkbox" onClick={onCheck}>
          {checked ? (
            <>
              <i
                className="fa-solid fa-check-double green"
                style={{ fontSize: "1.4rem" }}
              ></i>
              <p className="line"></p>
            </>
          ) : (
            <></>
          )}
        </span>
        {/* {!isEditable ? (
          <span className="toDoTitle">{props.taskName}</span>
        ) : (
          <span contentEditable={props.isEditable ? true : false}>
            {props.taskName}
          </span>
        )} */}
        <span className="toDoTitle" contentEditable={isEditing ? true : false}>
          {taskName}

          <i
            className="fa-solid fa-trash-can red"
            style={{ fontSize: "1.4rem" }}
            id={id}
            onClick={onSelect}
          ></i>
          {/* <i class="fa-solid fa-check"></i> */}
          <i
            className={`fa-solid ${
              isEditing ? "fa-check" : "fa-pen-to-square"
            }`}
            style={{ fontSize: "1.4rem" }}
            id={id}
            onClick={handleEditing}
          ></i>
        </span>
      </li>
    </>
  );
};
export default Task;
