import { useState } from "react";
import "./App.css";

const onSubmitForm = (e) =>{
  e.preventDefault();
  console.log(e);
}

function App() {

  let [listName,setListName] = useState("");
  let inputListName = ''
  const inputEvent = (e) =>{
    inputListName = e.target.value;
    console.log(e);
  }
  const onSubmitForm = () =>{
    setListName(inputListName);
  }


  return (
    <>
      <div className="container">
        <div className="heading">
          <p>date & time</p>
        </div>
        <div className="toDoContainer">
          <div className="addList">
            <form onSubmit={onSubmitForm}>
              <button>+</button>
              <input
                type="text"
                placeholder="Add To Do"
                id="toDoName"
                autoComplete="off"
                onChange ={inputEvent}
                value = {listName}
              />
            </form>
          </div>
          <ul className="listItem">
            <li className="toDoItem done">
              <p className="line"></p>
              <span className="checkbox">
                <i
                  className="fa-solid fa-check-double green"
                  style={{fontSize: "1.4rem"}}
                ></i>
              </span>
              <span className="toDoTitle">{listName}</span>
              <i
                className="fa-solid fa-trash-can red"
                style={{fontSize: "1.4rem"}}
              ></i>
            </li>
          </ul>
          <button id="clearBtn">Clear All</button>
        </div>
      </div>
    </>
  );
}

export default App;
