import { useEffect, useState } from "react";
import "./App.css";
import Task from "./Task";

let JSON_KEY = "List";

function App() {
  let [listName, setListName] = useState("");
  let [taskList, setTaskList] = useState([]);
  let [checked, setChecked] = useState(false);
  let [date, setDate] = useState(new Date());
  let inputListName = "";
  const saveList = () => {};
  const inputEvent = (e) => {
    inputListName = e.target.value;
    setListName(inputListName);
  };
  const onSubmitForm = (e) => {
    e.preventDefault();
  };

  const createList = () => {
    setListName(inputListName);
    if (listName === "") return;
    setTaskList((prevTaskList) => [...prevTaskList, listName]);
    console.log(taskList);
    localStorage.setItem(JSON_KEY, JSON.stringify([...taskList, listName]));
  };
  const deleteTask = (e) => {
    taskList = taskList.filter((task, i) => {
      return e.target.id != i;
    });
    setTaskList(taskList);
    localStorage.setItem(JSON_KEY, JSON.stringify(taskList));
  };
  const checkTask = (e) => {
    console.log("checkTask called");
    setChecked(!checked);
  };
  const clearList = () => {
    let newList = [...taskList];
    let i = 0;
    while (newList.length != 0) {
      newList.pop();
    }
    setTaskList(newList);
    localStorage.setItem(JSON_KEY, JSON.stringify(taskList));
    console.log(taskList);
    console.log(newList);
  };

  useEffect(() => {
    taskList = JSON.parse(localStorage.getItem(JSON_KEY)) || [];
  });

  return (
    <>
      <div className="container">
        <div className="heading">
          <p>date & time</p>
        </div>
        <div className="toDoContainer">
          <div className="addList">
            <form onSubmit={onSubmitForm}>
              <button onClick={createList} type="submit">
                +
              </button>
              <input
                type="text"
                placeholder="Add To Do"
                id="toDoName"
                autoComplete="off"
                onChange={inputEvent}
                value={listName}
              />
            </form>
          </div>
          <ul className="listItem">
            {taskList.map((task, i) => {
              return (
                <Task
                  taskName={task}
                  key={i}
                  id={i}
                  onSelect={deleteTask}
                  onCheck={checkTask}
                  checked={checked}
                />
              );
            })}
          </ul>
          <button id="clearBtn" onClick={clearList}>
            Clear All
          </button>
        </div>
      </div>
    </>
  );
}

export default App;
