import { useState } from "react";
import "./App.css";
import Task from "./Task";
import DeleteIcon from '@mui/icons-material/Delete';

let JSON_KEY = "List";

function App() {
  let [listName, setListName] = useState("");
  let [taskList, setTaskList] = useState(
    JSON.parse(localStorage.getItem(JSON_KEY)) || []
  );
  let inputListName = "";
  const saveList = () => {};
  const inputEvent = (e) => {
    inputListName = e.target.value;
    setListName(inputListName);
  };
  const onSubmitForm = (e) => {
    e.preventDefault();
  };

  const createList = () => {
    setListName(inputListName);
    setTaskList((prevTaskList) => [...prevTaskList, listName]);
    console.log(taskList);
    localStorage.setItem(JSON_KEY, JSON.stringify([...taskList, listName]));
  };
  const deleteTask = (e) => {
    taskList = taskList.filter((task, i) => {
      return e.target.id != i;
    });
    setTaskList(taskList);
    localStorage.setItem(JSON_KEY, JSON.stringify(taskList));
  };

  return (
    <>
      <div className="container">
        <div className="heading">
          <p>date & time</p>
        </div>
        <div className="toDoContainer">
          <div className="addList">
            <form onSubmit={onSubmitForm}>
              <button onClick={createList} type="submit">
                +
              </button>
              <input
                type="text"
                placeholder="Add To Do"
                id="toDoName"
                autoComplete="off"
                onChange={inputEvent}
                value={listName}
              />
            </form>
          </div>
          <ul className="listItem">
            <DeleteIcon/>
            {taskList.map((task, i) => {
              return (
                <Task taskName={task} key={i} id={i} onSelect={deleteTask} />
              );
            })}
          </ul>
          <button id="clearBtn">Clear All</button>
        </div>
      </div>
    </>
  );
}

export default App;
