import { isEditable } from "@testing-library/user-event/dist/utils";
import React from "react";
import { useState } from "react";
// import { taskList,setTaskList } from "./App";

const Task = (props) => {
  const [isEditing, setIsEditing] = useState(false);
  const handleEditing = (e) => {
    setIsEditing(!isEditing);
    taskList[e.target.id] = e.target.innerHTML;
    props.setTasklist(props.taskList);
    console.log("Editing : " + e.target.id);
  };
  return (
    <>
      <li
        className="toDoItem done"
        style={{ opacity: props.checked ? "50%" : "100%" }}
      >
        {props.checked ? (
          <>
            <p className="line"></p>
          </>
        ) : (
          <></>
        )}
        <span className="checkbox" onClick={props.onCheck}>
          {props.checked ? (
            <>
              <i
                className="fa-solid fa-check-double green"
                style={{ fontSize: "1.4rem" }}
              ></i>
              <p className="line"></p>
            </>
          ) : (
            <></>
          )}
        </span>
        {/* {!isEditable ? (
          <span className="toDoTitle">{props.taskName}</span>
        ) : (
          <span contentEditable={props.isEditable ? true : false}>
            {props.taskName}
          </span>
        )} */}
        <span className="toDoTitle" contentEditable={isEditing ? true : false}>
          {props.taskName}
        </span>

        <i
          className="fa-solid fa-trash-can red"
          style={{ fontSize: "1.4rem" }}
          id={props.id}
          onClick={props.onSelect}
        ></i>
        {/* <i class="fa-solid fa-check"></i> */}
        <i
          className={`fa-solid ${isEditing ? "fa-check" : "fa-pen-to-square"}`}
          style={{ fontSize: "1.4rem" }}
          id={props.id}
          onClick={handleEditing}
        ></i>
      </li>
    </>
  );
};
export default Task;
