import { useState } from "react";
import "./App.css";

const onSubmitForm = (e) => {
  alert('Submitted')
  e.preventDefault();
  console.log(e);
};

function App() {
  let [listName, setListName] = useState("");
  let [taskList, setTaskList] = useState([]);
  let inputListName = "";
  const inputEvent = (e) => {
    inputListName = e.target.value;
    setListName(inputListName);
    console.log(e);
  };
  const onSubmitForm = () => {
    setListName(inputListName);
  };

  const createList = () => {
    setListName(inputListName);
    setListName([...taskList, inputListName]);
  };

  return (
    <>
      <div className="container">
        <div className="heading">
          <p>date & time</p>
        </div>
        <div className="toDoContainer">
          <div className="addList">
            <form onSubmit={() => {alert('Submitted')}}>
              <button onClick={createList} type = "submit">+</button>
              <input
                type="text"
                placeholder="Add To Do"
                id="toDoName"
                autoComplete="off"
                onChange={inputEvent}
                value={listName}
              />
            </form>
          </div>
          <ul className="listItem">
            {taskList.map((task) => {
              return (
                <li className="toDoItem done">
                  <p className="line"></p>
                  <span className="checkbox">
                    <i
                      className="fa-solid fa-check-double green"
                      style={{ fontSize: "1.4rem" }}
                    ></i>
                  </span>
                  <span className="toDoTitle">{task}</span>
                  <i
                    className="fa-solid fa-trash-can red"
                    style={{ fontSize: "1.4rem" }}
                  ></i>
                </li>
              );
            })}
          </ul>
          <button id="clearBtn">Clear All</button>
        </div>
      </div>
    </>
  );
}

export default App;
